import React, { Fragment, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./myStyles.css";

export const RegisterUser = () => {
  // useState always retruns an array w/ 2 values. first val is your state
  // the second value is a function that allows you to update your state.
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: "",
  }); // hook

  const { name, email, password, password2 } = formData;
  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value }); // [e.target.name] makes it so we can use this for every feild dynamically

  const onSubmit = async (e) => {
    e.preventDefault();
    if (password !== password2) {
      console.log("Paswd do not match");
    } else {
      console.log(formData);
      //   // add code to send things to backend by calling post request
      //   const newUser = {
      //     name,
      //     email,
      //     password,
      //   };
      //   try {
      //     const config = {
      //       headers: {
      //         "Content-Type": "application/json",
      //       },
      //     };
      //     const body = JSON.stringify(newUser);
      //     const res = await axios.post("/api/users", body, config);
      //     console.log(res.data); // token
      //   } catch (err) {
      //     console.log(err.response.data);
      //   }
    }
  };

  return (
    <Fragment>
      <div className="sign-up-form">
        <h1>Sign Up Here</h1>
        <form>
          <input
            type="text"
            className="input-box"
            name="user_fn"
            placeholder="First Name"
          />

          <input
            type="text"
            className="input-box"
            name="user_ln"
            placeholder="Last Name"
          />

          <input
            type="email"
            className="input-box"
            name="user_email"
            placeholder="Your Email"
          />
          <input
            type="text"
            className="input-box"
            name="user_username"
            placeholder="Your Username"
          />
          <input
            type="password"
            className="input-box"
            name="user_pass"
            placeholder="Your Password"
            minlength="8"
          />
          <p> </p>
          <button type="submit" className="signup-btn" value="RegisterUser">
            Sign up
          </button>
          <hr></hr>
          <p className="or">OR</p>
          <p>
            Do you have an account ?<Link to="#!">Sign in</Link>
          </p>
        </form>
      </div>
    </Fragment>
  );
};

export default RegisterUser;
